import React, { Component, Fragment } from 'react';

import {HashRouter, BrowserRouter as Router, Switch,  Route, Link, NavLink} from 'react-router-dom'

import Home from './Home'
import About from './About'
import News from './News'
import User from './User'
import Merror from './Merror'
class Index extends Component{
    constructor(props){
        super(props);

    }
    render() {
        return (
                <Router>

                    <Fragment>
                        <div>
                            <NavLink exact to={'/'} replace activeStyle={{
                                fontWeight: "bold",
                                color: "red"
                            }}>主页</NavLink>
                            <NavLink to={'/about'} replace activeStyle={{
                                fontWeight: "bold",
                                color: "red"
                            }}>About</NavLink>
                            <NavLink to={'/news'} replace activeStyle={{
                                fontWeight: "bold",
                                color: "red"
                            }}>News</NavLink>
                            {/*<NavLink to={'/user/1?name=123'} replace activeStyle={{*/}
                                {/*fontWeight: "bold",*/}
                                {/*color: "red"*/}
                            {/*}}>User</NavLink>*/}
                            <NavLink to={'/user/1?name=123'} replace activeStyle={{
                                fontWeight: "bold",
                                color: "red"
                            }}>User</NavLink>
                        </div>
                        <Switch>
                            <Route exact path={'/'} component={Home} />
                            <Route path={'/about'} component={About} />
                            <Route path={'/news'} render={ (props)=> <News {...props} sex={'1'} /> } />
                            <Route path={'/user/:id'} component={User} />
                            <Route component={Merror} />
                        </Switch>

                    </Fragment>
                </Router>
        )
    }
}
export default Index;
